from gensim.models import Word2Vec
import numpy as np
import pandas as pd
from scipy import spatial

# Cell
class Corpus:
    """An iterator that yields sentences (lists of str)."""
    def __init__(self, docs):
        self.docs_clean = docs

    def __iter__(self):
        for doc in self.docs_clean:
            yield doc

